function Slot(t,i,e,s){this.speed=300,this.si=null,this.el=t,this.finSpeed=i,this.step=e,this.limit=s,this.finPos=null}function getSpeed(t){return t.speed}function startMotion(){var t=this,i=Math.floor(10*Math.random());this.speed>this.finSpeed&&(this.speed-=this.step),$(this.el).text(i),this.si=window.setTimeout(startMotion.bind(t),getSpeed(t))}function stopMotion(){var t=new CustomEvent("isFinal"),i=this,e=Math.floor(10*Math.random());this.speed<this.limit?(this.speed+=this.step,$(this.el).text(e),this.si=window.setTimeout(stopMotion.bind(i),getSpeed(i))):(document.getElementById(this.el.split("#")[1]).dispatchEvent(t),this.speed=300,clearInterval(this.si),$(this.el).text(i.finPos))}Slot.prototype.start=function(){var t=this;t.si=window.setTimeout(startMotion.bind(t),getSpeed(t))},Slot.prototype.stop=function(t){var i=this;i.finPos=t,clearInterval(i.si),i.si=window.setTimeout(stopMotion.bind(i),getSpeed(i))},Slot.prototype.reset=function(){this.speed=300,clearInterval(this.si)};