var helps = {
  ifCond: require('./defaults/ifCond'),
  ifEven: require('./defaults/ifEven'),
  ifOdd: require('./defaults/ifOdd'),
  ifThree: require('./defaults/ifThree'),
  ifThreeOne: require('./defaults/ifThreeOne'),
  ifThreeTwo: require('./defaults/ifThreeTwo'),
  multiOr: require('./defaults/multiOr')
};

function register(helperName) {
  if (helps.hasOwnProperty(helperName)) {
    helps[helperName]();
  }
}

function getHelper(helperName) {
  if (helps.hasOwnProperty(helperName)) {
    return helps[helperName];
  }

  return helps;
}

module.exports = {
  register: register,
  getHelper: getHelper
};
