var colors = require('colors');

var stage = function() {};
module.exports = new stage();

// if the proccess is success
stage.prototype.success = function(log) {
 console.log('[' + 'success'.green + '] ' + log); 
};

// if the process have error
stage.prototype.error = function(log) {
 console.error('[' + 'error'.red + '] ' + log); 
};

// if is in process 
stage.prototype.process = function(log) {
 console.log('[' + 'process'.blue + '] ' + log); 
};

// if the process have some info message
stage.prototype.info = function(log) {
 console.info('[' + 'info'.yellow + '] ' + log); 
};
